    @Test
    public void testLdapSearchZDE() throws LdapException {
        SearchOperation search = new SearchOperation(new DefaultConnectionFactory("ldap://ad.sandbox.internal:389"));
        SearchResponse searchResponse = search.execute(SearchRequest.builder()
                .dn("OU=sandbox,DC=ad,DC=sandbox,DC=netfoundry,DC=io")
                .filter("(&(objectClass=user))")
                .returnAttributes("sn","givenName", "samAccountName")
                .build());

        logger.info("Search response status : {}",searchResponse.isSuccess());

        searchResponse.getEntries().forEach(ldapEntry -> ldapEntry.getAttributes().forEach(ldapAttribute -> logger.info("Attribute Name : {}  Attribute Value : {}",ldapAttribute.getName(),ldapAttribute.getStringValue())));
    }


    @Test
    public void testApacheLdapSearchZDE() throws LdapException {

        ConnectionFactory connectionFactory = new DefaultConnectionFactory("ldap://localhost:389");

        SearchOperation search = new SearchOperation(connectionFactory);

        BindOperation bind = new BindOperation(connectionFactory);

        bind.execute(SimpleBindRequest.builder().dn("uid=admin,ou=system").password("secret").build());

        SearchResponse searchResponse = search.execute(SearchRequest.builder()
                .dn("ou=system")
                .filter("(objectClass=account)")
                .returnAttributes("uid")
                .build());

        logger.info("Search response status : {}, size : {}",searchResponse.isSuccess(),searchResponse.entrySize());

        searchResponse.getEntries().forEach(ldapEntry -> ldapEntry.getAttributes().forEach(ldapAttribute -> logger.info("Attribute Name : {}  Attribute Value : {}",ldapAttribute.getName(),ldapAttribute.getStringValue())));
    }

