    @Test
    public void testAddUser() throws LdapException {
      String identity =
          "";
      logger.info("ziti identity : {}", identity);
      ZitiContext zitiContext = new ZitiApp.CredentialBuilder().fromKey(identity).build().getContext();
      ZitiLdapConnectionConfig zitiLdapConnectionConfig =
          new ZitiLdapConnectionConfig.Builder().service("ztash-dir-svc").bindDn("cn=ZtashAdmin,dc=ztash,dc=io").bindPass("p@ssw0rd").build();
      ZitiLdapConnection zitiLdapConnection = new ZitiLdapConnection(zitiContext, zitiLdapConnectionConfig);
      zitiLdapConnection.open();
      AddRequest addRequest = AddRequest.builder().dn("cn=PrashantGs,ou=Users,dc=ztash,dc=io")
          .attributes(new LdapAttribute("objectClass", "inetOrgPerson"), new LdapAttribute("sn", "foouser786")).build();
      OperationHandle<AddRequest, AddResponse> operation = zitiLdapConnection.operation(addRequest);
      AddResponse addResponse = operation.execute();
      logger.info("Add response status for OU : {}", addResponse.isSuccess());
      zitiLdapConnection.close();

    }
    
